package JavaBasics;

import org.w3c.dom.Text;

public class javaLoopConcept {

	 javaLoopConcept javalc=new javaLoopConcept();
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		  
		int i=1;
		while (i<=10){
			
			System.out.println(i);
			i=i+1;
			
		}
		
    System.out.println("************");
		
    for(i=1;i<10;i++)
    {
    	System.out.println("i="+i);
    }
    
    System.out.println("******************");
    

	
	
	for(int k=10;k>=0;k--){
		
		System.out.println(k);
		
	}
	System.out.println("******************");
	
	}
	
	
	
}
